mapper.select:
SELECT p.* FROM project AS p, 
(SELECT ps.pro_id, COUNT(*) AS num FROM project_star ps GROUP BY ps.pro_id ORDER BY num DESC) AS t
WHERE p.status = 1 AND p.id = t.pro_id AND ${condition}

SELECT p.* FROM project AS p 
WHERE  p.status = 1 AND ${condition}
ORDER  BY creation_date DESC

SELECT p.* FROM project AS p 
WHERE p.status = 0 AND ${condition}
ORDER BY creation_date DESC

mapper.update:
UPDATE project 
SET content = #{content,jdbcType=VARCHAR},
contact = #{contact,jdbcType=VARCHAR},
recruit = #{recruit,jdbcType=LONGVARCHAR}
WHERE id = #{id}

UPDATE project 
SET status = #{status,jdbcType=TINYINT},
content = #{content,jdbcType=VARCHAR},
contact = #{contact,jdbcType=VARCHAR},
recruit = #{recruit,jdbcType=LONGVARCHAR}
WHERE id = #{id}
